mapper.select:
SELECT c.id,c.uid,u.username,c.display_id,c.cid,u.realname,c.pid,c.time,c.status,c.checked,c.submit_id, c.submit_time FROM contest_record c,user_info u, (SELECT status,uid,pid,cpid, MIN(submit_id) AS min_submit_id, MIN(submit_time) AS min_submit_time FROM contest_record GROUP BY status,uid,pid,cpid ) AS t
t.status = c.status AND t.uid=c.uid AND t.pid=c.pid AND c.uid = u.uuid AND t.cpid=c.cpid AND t.min_submit_id=c.submit_id AND t.min_submit_time=c.submit_time
AND c.status=#{status}
AND c.cid = #{cid}
ORDER BY c.checked ASC,c.submit_time ASC

SELECT cr.id,cr.cid,cr.uid,cr.pid,cr.cpid,cr.display_id,cr.submit_id,u.username,cr.status,cr.submit_time,
cr.time,cr.score,cr.use_time,cr.checked,cr.gmt_create,cr.gmt_modified,
u.gender,u.realname as realname,u.avatar,u.school,u.nickname
FROM
(SELECT uid,pid,cpid,MAX(time) AS time FROM contest_record


        cid=#{cid}


        (
            cr.cid = #{cid}
            OR cr.cid in

                #{id}

        )

    AND status IS NOT NULL

        AND uid!=#{contestCreatorUid}



            AND `time` BETWEEN 0 AND #{sealTime}


            AND `time` BETWEEN 0 AND #{endTime}



GROUP BY uid,pid,cpid) t,
contest_record cr,user_info u WHERE t.uid = cr.uid AND t.pid =cr.pid
AND t.cpid = cr.cpid AND u.uuid = cr.uid AND t.time = cr.time

SELECT cr.id,cr.cid,cr.uid,cr.pid,cr.cpid,cr.display_id,cr.submit_id,u.username,cr.status,cr.submit_time,
cr.time,cr.score,cr.use_time,cr.checked,cr.gmt_create,cr.gmt_modified,
u.gender,u.realname as realname,u.avatar,u.school,u.nickname
FROM
(SELECT uid,pid,cpid,MAX(score) AS score FROM contest_record


        cid=#{cid}


        (
            cr.cid = #{cid}
            OR cr.cid in

                #{id}

        )

    AND status IS NOT NULL

        AND uid!=#{contestCreatorUid}



            AND `time` BETWEEN 0 AND #{sealTime}


            AND `time` BETWEEN 0 AND #{endTime}



GROUP BY uid,pid,cpid) t,
contest_record cr,user_info u WHERE t.uid = cr.uid AND t.pid =cr.pid
AND t.cpid = cr.cpid AND u.uuid = cr.uid AND t.score = cr.score

SELECT cr.id,cr.cid,cr.uid,cr.pid,cr.cpid,cr.display_id,cr.submit_id,u.username,cr.status,cr.submit_time,
cr.time,cr.score,cr.use_time,cr.checked,cr.gmt_create,cr.gmt_modified,
u.gender,u.realname as realname,u.avatar,u.school,u.nickname
FROM contest_record cr,user_info u

    cr.uid = u.uuid

        AND cr.cid = #{cid}


        AND (
            cr.cid = #{cid}
            OR cr.cid in

                #{id}

        )
        AND #{time} >= cr.time

    AND cr.status IS NOT NULL
    AND cr.uid != #{contestCreatorUid}

ORDER BY cr.time ASC
