mapper.select:
select p.id
from training_problem tp,
     problem p
where tp.tid = #{tid}
  and tp.pid = p.id
  and p.auth = 1

select t.*
from tag t,
     problem_tag pt
where t.id = pt.tid
  and pt.pid = #{pid}

SELECT tp.*
from (
         select t.id
         FROM training_register tr,
              training t
         where tr.tid = t.id
           and t.auth = 'Private'
           and tr.uid = #{uid}
     ) tm
         left join
     training_problem tp
     on tm.id = tp.tid
where tp.pid = #{pid}

SELECT a.pid,a.tid
FROM
    (
        SELECT pid,tid FROM training_problem WHERE tid in

            #{tid}

    ) a
        left join
    (
        SELECT DISTINCT pid FROM user_acproblem where uid = #{uid}
    ) b
    on a.pid = b.pid
where b.pid is not null
